module Day1Spec where

import Test.Hspec
import Day1 as Day1

spec :: Spec
spec = do
  describe "Day1" $ do
    context "getFloor" $ do 
      it "returns 0 for (())" $ do
        Day1.getFloor "(())" `shouldBe` 0

      it "returns 0 for ()()" $ do
        Day1.getFloor "()()" `shouldBe` 0

      it "returns 3 for (((" $ do
        Day1.getFloor "(((" `shouldBe` 3

      it "returns 3 for (()(()(" $ do
        Day1.getFloor "(()(()(" `shouldBe` 3

      it "returns 3 for ))(((((" $ do
        Day1.getFloor "))(((((" `shouldBe` 3

      it "returns -1 for ())" $ do
        Day1.getFloor "())" `shouldBe` -1

      it "returns -1 for ))(" $ do
        Day1.getFloor "))(" `shouldBe` -1

      it "returns -3 for )))" $ do
        Day1.getFloor ")))" `shouldBe` -3

      it "returns -3 for )())())" $ do
        Day1.getFloor ")())())" `shouldBe` -3

      it "return 74 for day1Input" $ do
        Day1.getFloor day1Input `shouldBe` 74

    context "firstBasement" $ do
      it "returns 1 for )" $ do 
        Day1.firstBasement ")" `shouldBe` 1

      it "returns 5 for ()())" $ do
        Day1.firstBasement "()())" `shouldBe` 5

      it "returns 1795 for day1Input" $ do
        Day1.firstBasement day1Input `shouldBe` 1795

main :: IO ()
main = hspec spec

